import React from "react";

const PostPage = ({ title, body, comments }) => {
  return (
    <>
      <h1>{title}</h1>
      <p>{body}</p>

      {comments ? (
        <ul>
          {comments.map(comment => (
            <li>{comment.body}</li>
          ))}
        </ul>
      ) : null}
    </>
  );
};

export default PostPage;
