import React, { useEffect } from "react";
import { connect } from "react-redux";
import { getPosts } from "../src/actions/postsActions";
import PostsList from "../components/PostsList";
import _ from "lodash";

const PostsContainer = ({ posts, getPosts }) => {
  useEffect(() => {
    getPosts();

    // eslint-disable-next-line
  }, []);
  const sorted = _.filter(posts, post => {
    return post.title.trim() && post.body.trim();
  });
  const sortedPosts = _.sortBy(sorted, "id").reverse();
  return <PostsList posts={sortedPosts} />;
};
const mapStateToProps = state => ({
  posts: state.postsReducer.posts
});

// const mapDispatchToProps = dispatch => {
//   return {
//     increment: () => dispatch(increment()),
//     decrement: () => dispatch(decrement()),
//     reset: () => dispatch(reset())
//   };
// };

export default connect(mapStateToProps, { getPosts })(PostsContainer);
