import React, { useState } from "react";
import styled from "styled-components";
import Router from "next/router";

const Wrapper = styled.div`
  margin-top: 20px;
`;
const CreatePost = ({ createPost, isFetching }) => {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  const handleSubmit = e => {
    e.preventDefault();

    if (title.trim && body.trim) {
      createPost(title, body);
      Router.push("/");
    } else {
      console.log("Characters error");
    }
  };
  return (
    <>
      <h1 className="text-center display-3">Create your own post</h1>
      <Wrapper>
        <form onSubmit={handleSubmit}>
          <div class="form-group">
            <label for="exampleInputEmail1">Post title</label>
            <input
              value={title}
              onChange={e => setTitle(e.target.value)}
              type="text"
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
            <small id="emailHelp" className="form-text text-muted">
              Your title should be less than 70 characters
            </small>
          </div>
          <div className="form-group">
            <label for="exampleFormControlTextarea1">Post text</label>
            <textarea
              value={body}
              onChange={e => setBody(e.target.value)}
              className="form-control"
              id="exampleFormControlTextarea1"
              rows="3"
            ></textarea>
          </div>
          <button disabled={isFetching} type="submit" className="btn btn-dark">
            Submit
          </button>
        </form>
      </Wrapper>
    </>
  );
};

export default CreatePost;
